import react from 'react';
import './todo.css';

import React, { useState } from 'react';
import './TodoList.css';

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [editId, setEditId] = useState(null);
  const [filter, setFilter] = useState('All');

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleAddTodo = () => {
    if (inputValue.trim() !== '') {
      if (editId !== null) {
        const updatedTodos = todos.map((todo) =>
          todo.id === editId ? { ...todo, text: inputValue } : todo
        );
        setTodos(updatedTodos);
        setEditId(null);
      } else {
        setTodos([...todos, { id: Date.now(), text: inputValue, completed: false }]);
      }
      setInputValue('');
    }
  };

  const handleEditTodo = (id, text) => {
    setInputValue(text);
    setEditId(id);
  };

  const handleToggleComplete = (id) => {
    const updatedTodos = todos.map((todo) =>
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    );
    setTodos(updatedTodos);
  };

  const handleRemoveTodo = (id) => {
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  };

  const handleClearCompleted = () => {
    const updatedTodos = todos.filter((todo) => !todo.completed);
    setTodos(updatedTodos);
  };

  const filteredTodos = todos.filter((todo) => {
    if (filter === 'All') return true;
    if (filter === 'Active') return !todo.completed;
    if (filter === 'Completed') return todo.completed;
    return true;
  });

  return (
    <div className="todo-container">
      <h1 className="todo-title">To-Do List</h1>
      <div className="todo-input">
        <input
          type="text"
          placeholder="Enter your to-do"
          value={inputValue}
          onChange={handleInputChange}
        />
        <button className="add-button" onClick={handleAddTodo}>
          {editId !== null ? 'Save' : 'Add'}
        </button>
      </div>
      <div className="todo-filters">
        <button className={filter === 'All' ? 'active-filter' : ''} onClick={() => setFilter('All')}>
          All
        </button>
        <button className={filter === 'Active' ? 'active-filter' : ''} onClick={() => setFilter('Active')}>
          Active
        </button>
        <button className={filter === 'Completed' ? 'active-filter' : ''} onClick={() => setFilter('Completed')}>
          Completed
        </button>
      </div>
      <ul className="todo-list">
        {filteredTodos.map((todo) => (
          <li key={todo.id} className={todo.completed ? 'completed' : ''}>
            {editId === todo.id ? (
              <input
                type="text"
                value={inputValue}
                onChange={handleInputChange}
              />
            ) : (
              <span className="todo-text">{todo.text}</span>
            )}
            <div className="todo-actions">
              <button
                className="edit-button"
                onClick={() => handleEditTodo(todo.id, todo.text)}
              >
                Edit
              </button>
              <button
                className="complete-button"
                onClick={() => handleToggleComplete(todo.id)}
              >
                {todo.completed ? 'Undo' : 'Complete'}
              </button>
              <button
                className="remove-button"
                onClick={() => handleRemoveTodo(todo.id)}
              >
                Remove
              </button>
            </div>
          </li>
        ))}
      </ul>
      <button className="clear-button" onClick={handleClearCompleted}>
        Clear Completed
      </button>
    </div>
  );
};

export default TodoList;
